function MainClick({ handleClick, bossCount }) {
  const image = getImage(bossCount);

  function getImage() {
    if (bossCount === 2) {
      return tranformer;
    } else if (bossCount === 3) {
      return orange;
    }

    return scooter;
  }

    return (
        <img src={image} alt="" className="main" onClick={handleClick} />
    );
  }

  export default MainClick;
