import "./style.css";
import React, { useState, useEffect } from 'react';
import CountStatistic from "./components/CountStatistic";
import Upgrades from "./components/Upgrades";
import Boss from "./components/Boss";
import Spell from "./components/Spell";

const INITIAL_STATE = {
  number: 0,
  num: 100,
  HP: 300,
  x: 0,
  z: 0,
  n1: 5,
  n2: 20,
  n3: 40,
  n4: 80,
  threshold1: -1,
  threshold2: 10,
  threshold3: 25,
  threshold4: 70,
  k1: 0,
  k2: 0,
  k3: 0,
  k4: 0
};

const SHIELD_BOSSES = {
  1: 500,
  2: 1500
};

const BOSS_HP_COUNT = 700;
const BOSS_HP_MULTIPLIER = 4;
const BOSS_GOLD_REWARD = 500;
const SPELL_DURATION = 10;
const SPELL_COOLDOWN = 0;

function App() {
  const [bossCount, setBossCount] = useState(1);
  const [HPCount, setHPCount] = useState(BOSS_HP_COUNT);
  const [maxNumberReached, setMaxNumberReached] = useState(0);
  const [clonNum, setClonNum] = useState(0);
  const [spellDuration, setSpellDuration] = useState(false);
  const [kdDuration, setKdDuration] = useState(false);
  const [seconds, setSeconds] = useState(0);

  const [state, setState] = useState(INITIAL_STATE);

  const { number, num, z, x, n1, n2, n3, n4, k1, k2, k3, k4, HP, threshold1, threshold2, threshold3, threshold4 } = state;
  const [defense, setDefense] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setState(prevState => ({
        ...prevState,
        number: prevState.number + x,
        HP: prevState.HP - defense
      }));
      }, 1000);
      return () => clearInterval(timer);
      }, [x, defense]);
      
      useEffect(() => {
      if (spellDuration) {
      const spellTimer = setTimeout(() => {
      setSpellDuration(false);
      }, SPELL_DURATION * 1000);
      return () => clearTimeout(spellTimer);
      }
      }, [spellDuration]);
      
      useEffect(() => {
      if (kdDuration) {
      const kdTimer = setTimeout(() => {
      setKdDuration(false);
      }, SPELL_COOLDOWN * 1000);
      return () => clearTimeout(kdTimer);
      }
      }, [kdDuration]);
      
      const handleClicke = (boss) => {
      if (boss === 0) {
      const bossHP = BOSS_HP_COUNT + (bossCount - 1) * BOSS_HP_MULTIPLIER;
      const damage = num + (n1 * k1) + (n2 * k2) + (n3 * k3) + (n4 * k4);
      const newHPCount = HPCount - damage;
      const newBossCount = bossCount + 1;
      
        if (newHPCount <= 0) {
          setHPCount(bossHP);
          setBossCount(newBossCount);
          setMaxNumberReached(number);
          setState(prevState => ({
            ...prevState,
            number: 0,
            HP: bossHP,
            x: prevState.x + 1,
            z: prevState.z + 1
          }));
        } else {
          setHPCount(newHPCount);
          setState(prevState => ({
            ...prevState,
            number: prevState.number + 1,
            HP: newHPCount
          }));
        }
      } else {
        const bossHP = SHIELD_BOSSES[boss];
        const damage = num + (n1 * k1) + (n2 * k2) + (n3 * k3) + (n4 * k4);
        const newBossHP = bossHP - damage;
      
        if (newBossHP <= 0) {
          setHPCount(BOSS_HP_COUNT);
          setBossCount(boss + 1);
          setMaxNumberReached(number);
          setState(prevState => ({
            ...prevState,
            number: 0,
            HP: BOSS_HP_COUNT,
            x: prevState.x + 1,
            z: prevState.z + 1
          }));
        } else {
          setHPCount(newBossHP);
          setState(prevState => ({
            ...prevState,
            number: prevState.number + 1,
            HP: newBossHP
          }));
        }
      }
      };
      
      const handleSpellButtonClick = () => {
      if (!spellDuration && !kdDuration) {
      setSpellDuration(true);
      setDefense(defense + 1);
      setTimeout(() => {
      setDefense(defense - 1);
      setKdDuration(true);
      }, SPELL_DURATION * 1000);
      }
      };
      
      return (
      <div className="App">
      <div className="container">
      <CountStatistic number={number} maxNumberReached={maxNumberReached} seconds={seconds} setSeconds={setSeconds} />
      
          <div className="main_inner">
            <div className="spells">
              <Spell handleSpellButtonClick={handleSpellButtonClick} seconds={seconds} />
            </div>
            <Boss handleClicks={() => handleClicke(0)} bossCount={bossCount} HP1={HP} defense={defense} number={number} />
          </div>
          <Upgrades
            number={number}
            n1={n1}
            n2={n2}
            n3={n3}
            n4={n4}
            k1={k1}
            k2={k2}
            k3={k3}
            k4={k4}
            threshold1={threshold1}
            threshold2={threshold2}
            threshold3={threshold3}
            threshold4={threshold4}
            maxNumberReached={maxNumberReached}
            handleClicke={handleClicke}
            spellDuration={spellDuration}
          />
        </div>
      </div>
      );
      }
      
      export default App;
