import "./style.css";
import scooter from "./img/samokat.png";
import React, { useState, useEffect } from 'react';

function App() {
  const [number, setNumber] = useState(0);
  const [num, setNum] = useState(1);
  const [x, setX] = useState(0);
  const [upgrades, setUpgrades] = useState([
    { cost: 20, purchases: 0, increment: 1 },
    { cost: 50, purchases: 0, increment: 1 },
    { cost: 150, purchases: 0, increment: 1 }
  ]);

  useEffect(() => {
    const timer = setInterval(() => {
      setNumber(prevNumber => prevNumber + x);
    }, 1000);

    return () => clearInterval(timer);
  }, [x]);

  const handleClick = () => {
    setNumber(prevNumber => prevNumber + num);
  };

  const handleUpgradeClick = (index) => {
    const newUpgrades = [...upgrades];
    const upgrade = newUpgrades[index];

    upgrade.purchases++;
    upgrade.increment = upgrade.purchases > 40 ? 5 :
                        upgrade.purchases > 30 ? 4 :
                        upgrade.purchases > 20 ? 3 :
                        upgrade.purchases > 10 ? 2 :
                        upgrade.purchases > 0 ? 1 : 0;

    setNum(num + upgrade.increment);
    setNumber(prevNumber => prevNumber - upgrade.cost);
    setUpgrades(newUpgrades);
  };

  const handleXClick = (increment) => {
    setX(prevX => prevX + increment);
  };

  return (
    <div>
      <div className="mains">
        <div className="container">
          <div className="main_inner">
            <div className="main_click">
              <img src={scooter} alt="" className="main" width="500px" onClick={handleClick} />
              <span className="main_count" id="count">{number}</span>
              <span className="main_count" id="count">За 1 клик: {num}</span>
              <span className="main_count" id="count">За 1 секунду: {x}</span>
            </div>
          </div>
        </div>
        <div className="main_up">
          {upgrades.map((upgrade, index) => (
            <button
              key={index}
              className="btn cl"
              disabled={number < upgrade.cost}
              onClick={() => handleUpgradeClick(index)}
            >
              Турбо самокат. Стоимость: {upgrade.cost} Число покупок: {upgrade.purchases}
            </button>
          ))}
          <button className="btn2 cl" onClick={() => handleXClick(1)}>Click to buy an upgrade for 200</button>
          <button className="btn3 cl" onClick={() => handleXClick(2)}>Click to buy an upgrade for 400</button>
        </div>
      </div>
    </div>
  );
}

export default App;
