import "./style.css";
import scooter from "./img/samokat.png";
import React, { useState, useEffect } from 'react';
import Button from "./components/button";

function App() {
  const [number, setNumber] = useState(0);
  const [num, setNum] = useState(1);
  const [x, setX] = useState(0);
  const [n1, setN1] = useState(20);
  const [n2, setN2] = useState(50);
  const [n3, setN3] = useState(150);
  const [k1, setK1] = useState(0);
  const [k2, setK2] = useState(0);
  const [k3, setK3] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setNumber(prevNumber => prevNumber + x);
    }, 1000);

    return () => clearInterval(timer);
  }, [x]);

  const handleClick = () => {
    setNumber(prevNumber => prevNumber + num);
  };

  const increasingCost = (n) => {
    setNumber(prevNumber => prevNumber - n);
  }

  const handleClicks = (n, z, cost ) => {
    setX(prevX => prevX + n);
    setK2(prevK => prevK + 1);
    increasingCost(z);
    cost(prevN => prevN * 2);
  }

  const incrementX = (k1) => {
    let newNum = num + 1;
    let purchases = 10
    let n = 1
    if (k1 > purchases) {
      newNum += n
      purchases += 10
      n++
    }
    setK1(prevK1 => prev)
    return newNum
  }


  const handleClick1 = () => {

    setNum(incrementX(k1));
    handleClicks(0, n1, setN1)
  };

  const handleClick2 = () => {
    handleClicks(1, n2, setN2)
  };

  const handleClick3 = () => {
    handleClicks(2, n3, setN3)
  };

  return (
    <div>
      <div className="mains">
        <div className="container">
          <div className="main_inner">
            <div className="main_click">
              <img src={scooter} alt="" className="main" width="500px" onClick={handleClick} />
              <span className="main_count" id="count">{number}</span>
              <span className="main_count" id="count">За 1 клик: {num}</span>
              <span className="main_count" id="count">За 1 секунду: {x}</span>
            </div>
          </div>
        </div>
        <div className="main_up">
          <Button title = {"Деревянный самокат"} number = {number} n ={n1} handleClick = {handleClick1} k = {k1}/>
          <Button title = {"Медный самокат"} number = {number} n ={n2} handleClick = {handleClick2} k = {k2}/>
          <Button title = {"Железный самокат"} number = {number} n ={n3} handleClick = {handleClick3} k = {k3}/>
        </div>
      </div>
    </div>
  );
}

export default App;
