import { useState, useEffect } from "react";

const ButtonDPS = ({ title, n, ns}) => {
    const [number, setNumber] = useState(ns);
    const [x, setX] = useState(0);
    const [n1, setN1] = useState(n);
    const [k1, setK1] = useState(0);
     
      const handleClick = () => {
        setX(prevX => prevX + n1);
        setK1(prevK => prevK + 1);
        setNumber(prevNumber => prevNumber - n1);
        setN1(prevN => prevN * 1.5);
      };

    return ( 
        <button className="btn cl" disabled={ns < n} onClick={handleClick}>{title}. Стоимость: {n.toFixed(0)} Число покупок: {k1}</button>
     );
}

const effect = () => {
    useEffect(() => {
        const timer = setInterval(() => {
            setNumber(prevNumber => prevNumber + x);
        }, 1000);
        
        return () => clearInterval(timer);
      }, [x]);
}

export default ButtonDPS;
