import React from 'react';
import Button from './button';

const Upgrades = ({ number, n1, n2, n3, n4, k1, k2, k3, k4, threshold1, threshold2, threshold3, threshold4, maxNumberReached, handleClicke, spellDuration }) => {
  return (
    <div className="main_up">
      <Button title="Клик" number={number} n={n1} handleClick={() => handleClicke(1)} k={k1} threshold={threshold1} maxnumber={maxNumberReached} spellDuration = {spellDuration} />
      <Button title="Денег в секунду" number={number} n={n2} handleClick={() => handleClicke(2)} k={k2} threshold={threshold2} maxnumber={maxNumberReached} spellDuration = {spellDuration} />
      <Button title="Урон в секунду (дерево)" number={number} n={n3} handleClick={() => handleClicke(3)} k={k3} threshold={threshold3} maxnumber={maxNumberReached} spellDuration = {spellDuration} />
      <Button title="Урон в секунду (камень)" number={number} n={n4} handleClick={() => handleClicke(4)} k={k4} threshold={threshold4} maxnumber={maxNumberReached} spellDuration = {spellDuration} />
    </div>
  );
};

export default Upgrades;
