import React, { useState, useEffect } from 'react';

const Button = ({ number, HP, n, handleClick, k, classs }) => {

    const [cost, setCost] = useState(0)
    const [price, setPrice] = useState(50)
    const [dps, setDps] = useState(0)
    const [dollars, setDollars] = useState(number)

    useEffect(() => {
        const timer = setInterval(() => {
          setDollars(prevDollars => prevDollars + dps);
        }, 1000);
    
        return () => clearInterval(timer);
      }, [dps]);

    const handleClicks = (n, z, cost, k) => {
        setState(prevState => ({
          ...prevState,
          x: prevState.x + n,
          [k]: prevState[k] + 1,
          number: prevState.number - z,
          [cost]: prevState[cost] * 1.1
        }));
      };

    const [buttons, setButtons] = useState([
        { title: 'Улучшение 1. ', threshold: 50, cost: cost, price: price, income: 1 }
      ]);
    return ( 
        <div>
        {buttons.map((button, index) => (
            <button
              key={index}
              className={number > button.threshold ? 'active' : ''}
              onClick={() => handleClick}
            ><p>
              {button.title}<br />
                Стоимость улучшения: {button.price}$.<br />
                Количество улучшений: {button.cost}
            </p>
            </button>
          ))}
        </div>
     );
}

export default Button;
