import "./style.css";
import scooter from "./img/samokat.png";
import React, { useState, useEffect } from 'react';

function App() {
  const [number, setNumber] = useState(0);
  const [num, setNum] = useState(1);
  const [x, setX] = useState(0);
  const [upgradeCost, setUpgradeCost] = useState(20);
  const [upgradeIncrement, setUpgradeIncrement] = useState(1);
  const [upgradeClicks, setUpgradeClicks] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setNumber(prevNumber => prevNumber + x);
    }, 1000);

    return () => clearInterval(timer);
  }, [x]);

  const handleClick = () => {
    setNumber(prevNumber => prevNumber + num);
  };

  const handleUpgradeClick = () => {
    if (upgradeClicks >= 10) {
      setNum(prevNum => prevNum + upgradeIncrement);
      setUpgradeIncrement(prevIncrement => prevIncrement + 1);
    }
    setNumber(prevNumber => prevNumber - upgradeCost);
    setUpgradeCost(prevCost => prevCost + 10);
    setUpgradeClicks(prevClicks => prevClicks + 1);
  };

  const handleXClick = (increment) => {
    setX(prevX => prevX + increment);
  };

  return (
    <div>
      <div className="mains">
        <div className="container">
          <div className="main_inner">
            <div className="main_click">
              <img src={scooter} alt="" className="main" width="500px" onClick={handleClick} />
              <span className="main_count" id="count">{number}</span>
              <span className="main_count" id="count">За 1 клик: {num}</span>
              <span className="main_count" id="count">За 1 секунду: {x}</span>
            </div>
          </div>
        </div>
        <div className="main_up">
          <button className="btn cl" disabled={number < upgradeCost} onClick={handleUpgradeClick}>
            Турбо самокат. Стоимость: {upgradeCost} Число покупок: {upgradeClicks}
          </button>
          <button className="btn2 cl" onClick={() => handleXClick(1)}>Click to buy an upgrade for 200</button>
          <button className="btn3 cl" onClick={() => handleXClick(2)}>Click to buy an upgrade for 400</button>
        </div>
      </div>
    </div>
  );
}

export default App;
