const Button = ({ title, number, n, handleClick, k }) => {
    const formattedN = n.toFixed(0);

    return (
      <div className="main_up">
        <img src={scooter} alt="" className="main" width="500px" onClick={handleClick} />
        <span className="main_count" id="count">{title}</span>
        <span className="main_count" id="count">Количество: {formattedN}</span>
        <span className="main_count" id="count">Цена: {number.toFixed(0)}</span>
        <button onClick={handleClick}>Купить</button>
      </div>
    )
}

export default Button;
