import { useState, useEffect } from "react";

const ButtonDPS = ({ title}) => {
    const [number, setNumber] = useState(0);
    const [x, setX] = useState(0);
    const [n1, setN1] = useState(0);
    const [k1, setK1] = useState(0);

    useEffect(() => {
        const timer = setInterval(() => {
            setNumber(prevNumber => prevNumber + x);
        }, 1000);
        
        return () => clearInterval(timer);
      }, [x]);

    const increasingCost = (n) => {
        setNumber(prevNumber => prevNumber - n);
      }

    const handleClicks = (n, z, cost, c) => {
        setX(prevX => prevX + n);
        setNumber(prevK => prevK + 1);
        increasingCost(z);
        cost(prevN => prevN * 1.5);
      }
      
      const handleClick = () => {
        handleClicks(1, n1, setN1, setK1);
      };
    return ( 
        <button className="btn cl" disabled={number < n1} onClick={handleClick}>{title}. Стоимость: {n1.toFixed(0)} Число покупок: {k1}</button>
     );
}

export default ButtonDPS;
