import scooter from "../img/samokat.png";
import tranformer from "../img/300_-transformed.png";
import orange from "../img/apelsin.png"



function MainClick({ handleClick, number, bossCount }) {
  const slider = () => {
    return bossCount = 1 ? tranformer : bossCount = 2 ? orange : scooter
  }

  function getImage(bossCount) {
    if (bossCount === 1) {
      return transformer; // Путь к изображению первого босса
    } else if (bossCount === 1) {
      return "boss2.png"; // Путь к изображению второго босса
    } else if (bossCount === 2) {
      return "boss3.png"; // Путь к изображению третьего босса
    }
    // Добавьте условия для других значений bossCount и соответствующие пути к изображениям
    return "scooter"; // Путь к изображению по умолчанию
  }

    return (
      <div className="main_click" onClick={handleClick}>
        <img src={slider()} alt="" className="main" width="500px" />
      </div>
    );
  }

  export default MainClick;
