function effect(x) {
    return ( 
        useEffect(() => {
            const timer = setInterval(() => {
                setNumber(prevNumber => prevNumber + x);
            }, 1000);
            
            return () => clearInterval(timer);
          }, [x])
     );
}

export default effect;
