const Button = ({ title, number, n, handleClick, k, threshold, maxnumber, spellDuration }) => {
    return ( 
        <button className={`btn cl ${maxnumber > threshold ? 'active' : ''} ${number < n ? 'disabled' : '' || spellDuration == true ? 'disabled' : ''}`}  disabled={number < n} onClick={handleClick}>{title}<br/> Стоимость: {n}<br/> Число покупок: {k}</button>
     );
}

export default Button;
