import React, { useEffect } from 'react';

const Timer = ({ x, defense, setState }) => {
  useEffect(() => {
    const timer = setInterval(() => {
      if (defense > 0) {
      } else {
        setState(prevState => ({
          ...prevState,
          number: prevState.number + x,
          HP: prevState.HP - x,
          maxnumber: prevState.maxnumber + x
        }));
      }
    }, 1000);

    return () => clearInterval(timer);
  }, [x, defense, setState]);

  return null;
};

export default Timer;
