    //       [cost]: prevState[cost] * 1.1
    //     }));
    //   };

    const [buttons, setButtons] = useState([
        { title: 'Кнопка 1', threshold: 50, cost: cost, price: price }
      ]);
    return ( 
        <div>
        {buttons.map((button, index) => (
            <button
              key={index}
              className={number > button.threshold ? 'active' : ''}
              onClick={() => handleClick}
            >
              {button.title}
              
              {button.cost}
            </button>
          ))}
        </div>
     );
}

export default Button;
