import "./style.css"
import scooter from "./img/samokat.png"
import React, { useState, useEffect } from 'react';

function App() {
  const [number, setNumber] = useState(0);
  const [num, setNum] = useState(1);
  const [x, setX] = useState(0);
  let [k, setK] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setNumber(number => number + x)
    }, 1000);

    return () => clearInterval(timer);
  });

  const handleClick = () => {
     setNumber(number + num)
  };

  const handleClick1 = () => {
    if (k < 10) {
      setNum(n => n + 1)
    } else {
      setNum(n => n + 2)
    }
    setK(k=> k +1)
    console.log(k + " " + num)
  };

  const handleClick2 = () => {
    setX(x => x + 1)
  };

  const handleClick3 = () => {
    setX(x => x + 2)
  };

  return (
    <div>
      <div className="mains">
        <div className="container">
            <div className="main_inner">
                <div className="main_click">
                    <img src={scooter} alt="" className="main" width="500px" onClick = {handleClick} />
                    <span className="main_count" id="count">{number}</span>
                    <span className="main_count" id="count">За 1 клик: {num}</span>

                </div>
            </div>
        </div>
        <div className="main_up">
            <button className="btn cl" onClick = {handleClick1}>Турбо самокат.Стоимость:  Число покупок: {k}</button>
            <button className="btn2 cl" onClick = {handleClick2}>Click to buy an upgrade for 200</button>
            <button className="btn3 cl" onClick = {handleClick3}>Click to buy an upgrade for 400</button>
        </div>
      </div>
    </div>
  );
}

export default App;
