import React, { useState, useEffect } from 'react';

function Spell({ handleSpellButtonClick, seconds, kdDuration }) {
    const [spellDuration, setSpellDuration] = useState(false);
    const [clonNum, setClonNum] = useState(0);
    const [kdSeconds, setKdSeconds] = useState(180);
  
    const spellOff = () => {
      setSpellDuration(false);
      setKdDuration(true);
      setClonNum(0);
      setKdSeconds(180);
    };
  
    const spellOn = () => {
      setKdDuration(false);
      setSpellDuration(true);
    };
  
    useEffect(() => {
      if (spellDuration) {
        setTimeout(() => {
          spellOff();
        }, 10000);
      }
      if (kdDuration) {
        setTimeout(() => {
          spellOn();
        }, 180000);
      }
    }, [spellDuration, kdDuration]);
  
    useEffect(() => {
      let countdown;
  
      if (spellDuration || kdDuration) {
        countdown = setInterval(() => {
          if (spellDuration) {
            setSeconds(prevSeconds => prevSeconds - 1);
        }
