      };

    const [buttons, setButtons] = useState([
        { title: 'Улучшение 1. ', threshold: 25, cost: 0, price: 50, income: 1, handleClicks: handleClicks() }
      ]);
    return ( 
        <div>
        {buttons.map((button, index) => (
            <button
              key={index}
