import "./style.css"
import scooter from "./img/samokat.png"

function App() {
  return (
    <div>
      <div className="mains">
        <div className="container">
            <div className="main_inner">
                <div className="main_click">
                    <img src={scooter} alt="" className="main" width="500px" />
                    <span className="main_count" id="count">0</span>
                </div>
            </div>
        </div>
        <div className="main_up">
            <button className="btn cl" disabled>Click to buy an upgrade for 50</button>
            <button className="btn2 cl" disabled>Click to buy an upgrade for 200</button>
            <button className="btn3 cl" disabled>Click to buy an upgrade for 400</button>
        </div>
      </div>
    </div>
  );
}

let number = 0;
let num = 1;
let 
    n = 50, n2 = 150, n3 = 300,
    m = 1000,
    x = 2, x2 = 3, x3 = 1;

function incrementNumber() {
    number += num
    document.getElementById("count").textContent = number.toFixed(0);
    if (number > n) {
        document.querySelector(".btn").disabled = false;
    } 
    if (number > n2) {
        document.querySelector(".btn2").disabled = false;
    } 
    if (number > n3) {
        document.querySelector(".btn3").disabled = false;
    } 
}

function intervalNumber() {
    number += x3
    document.getElementById("count").textContent = number;
}

function click ()  {
    incrementNumber();
};

document.querySelector(".btn").onclick = function() {
    let intervalId = setInterval(intervalNumber, m);
    number -= n;
    n *= 1.5
    x3 *= 1.1
    if (number < n) {
        document.querySelector(".btn").disabled = true;
    }
};

document.querySelector(".btn2").onclick = function() {
    num = x
    n2 *= 2
    x *= 1.5
    if (number < n2) {
        document.querySelector(".btn2").disabled = true;
    }
};

document.querySelector(".btn3").onclick = function() {
    num = x2
    n3 *= 2
    x2 *= 1.5
    if (number < n3) {
        document.querySelector(".btn3").disabled = true;
    }
};



export default App;
