import { useState, useEffect } from "react";

const ButtonDPS = ({ title, number, n, k, c }) => {
    const [num, setNum] = useState(1);
    const [x, setX] = useState(0);
    const [n1, setN1] = useState(0);
    const [k1, setK1] = useState(0);

    useEffect(() => {
        const timer = setInterval(() => {
          c(prevNumber => prevNumber + x);
        }, 1000);
        
        return () => clearInterval(timer);
      }, [x]);

    const increasingCost = (n) => {
        c(prevNumber => prevNumber - n);
      }

    const handleClicks = (n, z, cost, c) => {
        setX(prevX => prevX + n);
        c(prevK => prevK + 1);
        increasingCost(z);
        cost(prevN => prevN * 1.5);
      }
    
      const incrementX = (k1) => {
        const purchases = 10;
        const n = k1 > purchases ? 2 : 1;
        return num + n;
      };
      
      const handleClick = () => {
        handleClicks(1, n3, setN3, setK3);
      };
    return ( 
        <button className="btn cl" disabled={number < n} onClick={handleClick}>{title}. Стоимость: {n.toFixed(0)} Число покупок: {k}</button>
     );
}

export default ButtonDPS;
