import "./style.css"
import scooter from "./img/samokat.png"
import React, { useState, useEffect } from 'react';

function App() {
  return (
    <div>
      <div className="mains">
        <div className="container">
            <div className="main_inner">
                <div className="main_click">
                    <img src={scooter} alt="" className="main" width="500px" onClick = {click} />
                    <span className="main_count" id="count">0</span>
                </div>
            </div>
        </div>
        <div className="main_up">
            <button className="btn cl" disabled onClick = {click1}>Click to buy an upgrade for 50</button>
            <button className="btn2 cl" disabled onClick = {click2}>Click to buy an upgrade for 200</button>
            <button className="btn3 cl" disabled onClick = {click3}>Click to buy an upgrade for 400</button>
        </div>
      </div>
    </div>
  );
}

const [number, setNumber] = useState(0);
const [num, setNum] = useState(1);
const [n, setN] = useState(50);
const [n2, setN2] = useState(150);
const [n3, setN3] = useState(300);
const [m, setM] = useState(1000);
const [x, setX] = useState(2);
const [x2, setX2] = useState(3);
const [x3, setX3] = useState(1);

useEffect(() => {
  if (number > n) {
    document.querySelector('.btn').disabled = false;
  }
  if (number > n2) {
    document.querySelector('.btn2').disabled = false;
  }
  if (number > n3) {
    document.querySelector('.btn3').disabled = false;
  }
}, [number, n, n2, n3]);

const incrementNumber = () => {
  setNumber(number + num);
};

const intervalNumber = () => {
  setNumber(number + x3);
};

const handleClick = () => {
  incrementNumber();
};

const handleClick1 = () => {
  let intervalId = setInterval(intervalNumber, m);
  setNumber(number - n);
  setN(n * 1.5);
  setX3(x3 * 1.1);
  if (number < n) {
    document.querySelector('.btn').disabled = true;
  }
};

const handleClick2 = () => {
  setNum(x);
  setN2(n2 * 2);
  setX(x * 1.5);
  if (number < n2) {
    document.querySelector('.btn2').disabled = true;
  }
};

const handleClick3 = () => {
  setNum(x2);
  setN3(n3 * 2);
  setX2(x2 * 1.5);
  if (number < n3) {
    document.querySelector('.btn3').disabled = true;
  }
};



export default App;
