import "./style.css"
import scooter from "./img/samokat.png"
import React, { useState, useEffect } from 'react';

function App() {
  let number = 0
  let [num, setNum] = useState(1);
  const [n, setN] = useState(50);
  const [n2, setN2] = useState(15);
  const [n3, setN3] = useState(300);
  const [x, setX] = useState(2);
  const [x2, setX2] = useState(3);
  const [x3, setX3] = useState(1);


  const intervalNumber = () => {
    number++
  };
  let intervalId = setInterval(intervalNumber, 1000);

  const handleClick = () => {
    number += num
  };

  const handleClick2 = () => {
    setNum(2)
  };

  const handleClick3 = () => {
    setNum(3);
    number -= n3
    setN3(n3 * 2);
    setX2(x2 * 1.5);
    if (number < n3) {
      document.querySelector('.btn3').disabled = true;
    }
  };
  return (
    <div>
      <div className="mains">
        <div className="container">
            <div className="main_inner">
                <div className="main_click">
                    <img src={scooter} alt="" className="main" width="500px" onClick = {handleClick} />
                    <span className="main_count" id="count">{number}</span>
                </div>
            </div>
        </div>
        <div className="main_up">
            <button className="btn cl" onClick = {handleClick2}>Click to buy an upgrade for 50</button>
            <button className="btn2 cl" onClick = {handleClick2}>Click to buy an upgrade for 200</button>
            <button className="btn3 cl" disabled onClick = {handleClick3}>Click to buy an upgrade for 400</button>
        </div>
      </div>
    </div>
  );
}

export default App;
