import React, { useState } from 'react';

const Button = ({ number, n, handleClick, k, classs }) => {

    const handleClicks = (n, z, cost, k) => {
        setState(prevState => ({
          ...prevState,
          x: prevState.x + n,
          [k]: prevState[k] + 1,
          number: prevState.number - z,
          [cost]: prevState[cost] * 1.1
        }));
      };
      
    const [buttons, setButtons] = useState([
        { title: 'Кнопка 1', threshold: 50 },
        { title: 'Кнопка 2', threshold: 100 },
        { title: 'Кнопка 3', threshold: 200 },
      ]);
    return ( 
        <div>
        {buttons.map((button, index) => (
            <button
              key={index}
              className={number > button.threshold ? 'active' : ''}
              onClick={() => handleClick}
            >
              {button.title}
            </button>
          ))}
        </div>
     );
}

export default Button;
