import React from 'react';

const CountStatistic = ({ number, maxNumberReached, seconds, setSeconds }) => {
  const formattedNumber = number ? number.toFixed(2) : '0.00';
  const formattedMaxNumberReached = maxNumberReached ? maxNumberReached.toFixed(2) : '0.00';

  return (
    <div>
      <div>Number: {formattedNumber}</div>
      <div>Max Number Reached: {formattedMaxNumberReached}</div>
      <div>Seconds: {seconds}</div>
      <button onClick={() => setSeconds(seconds + 1)}>Increase Seconds</button>
    </div>
  );
};

export default CountStatistic;
