import "../style.css"
import React from 'react';

const CountStatistic = ({ number, HP, bossStats, shieldBosses, defense }) => {
  const bossHP = bossStats.HP.toFixed(0);
  const maxHP = bossStats.count * bossStats.HP;
  const shieldHP = shieldBosses[bossStats.count].toFixed(0);
  
  return (
    <div className="count-statistic">
      <div className="count-statistic__item">
        <span>Number:</span>
        <span>{number.toFixed(0)}</span>
      </div>
      <div className="count-statistic__item">
        <span>HP:</span>
        <span>{HP}</span>
      </div>
      <div className="count-statistic__item">
        <span>Boss HP:</span>
        <span>{bossHP}</span>
      </div>
      <div className="count-statistic__item">
        <span>Max HP:</span>
        <span>{maxHP}</span>
      </div>
      <div className="count-statistic__item">
        <span>Shield HP:</span>
        <span>{shieldHP}</span>
      </div>
      <div className="count-statistic__item">
        <span>Defense:</span>
        <span>{defense}</span>
      </div>
    </div>
  );
};

export default CountStatistic;
