import "./style.css";
import React, { useState, useEffect } from 'react';
import MainClick from "./components/MainScooter";
import CountStatistic from "./components/CountStatistic";
import Upgrades from "./components/Upgrades";

function App() {
  const [bossStats, setBossStats] = useState({ count: 1, HP: 700 });
  
  const [state, setState] = useState({
    number: 0,
    num: 1,
    HP: 300,
    x: 0,
    z: 0,
    upgrades: {
      n1: 50,
      n2: 150,
      n3: 300,
      n4: 500,
      threshold1: -1,
      threshold2: 100,
      threshold3: 200,
      threshold4: 400,
      k1: 0,
      k2: 0,
      k3: 0,
      k4: 0
    }
  });

  const { number, num, z, x, upgrades, HP } = state;
  const [defense, setDefense] = useState(0);
  const shieldBosses = {
    1: 500,
    2: 1500
  };

  useEffect(() => {
    const intervalDuration = 1000;

    const interval = setInterval(() => {
      setState(prevState => ({
        ...prevState,
        number: prevState.number + prevState.x * intervalDuration,
        HP: prevState.HP - prevState.x * intervalduration,
        maxnumber: prevState.maxnumber + prevState.x * intervalDuration
}));
}, intervalDuration);

return () => clearInterval(interval);
}, [x]);

const handleClick = (index, n, z, cost, k, p) => {
setState(prevState => ({
...prevState,
x: prevState.x + n,
z: prevState.z + p,
[k]: prevState[k] + 1,
number: prevState.number - z,
[cost]: prevState[cost] * 1.1
}));

if (index === 0 && defense > 0) {
  setDefense(prevDefense => prevDefense - num);
} else {
  setState(prevState => ({
    ...prevState,
    number: prevState.number + num,
    HP: prevState.HP - num
  }));
}
};

const handleClicke = (index) => {
const upgrades = [
{ index: 0, n: n1, z: 0, cost: "n1", k: "k1", p: 0 },
{ index: 1, n: n2, z: 0, cost: "n2", k: "k2", p: 0 },
{ index: 2, n: n3, z: 0, cost: "n3", k: "k3", p: 2 },
{ index: 3, n: n4, z: 0, cost: "n4", k: "k4", p: 3.5 }
];

const { n, z, cost, k, p } = upgrades[index];
handleClick(index, n, z, cost, k, p);
};

const [spellState, setSpellState] = useState({
spellDuration: false,
kdDuration: false,
clonNum: 0,
seconds: 0
});

const { spellDuration, kdDuration, clonNum, seconds } = spellState;

useEffect(() => {
if (spellDuration) {
const countdown = setInterval(() => {
setSpellState(prevState => ({
...prevState,
clonNum: prevState.clonNum - 1
}));
}, 1000);

  return () => clearInterval(countdown);
}
}, [spellDuration]);

const nn = () => {
if (kdDuration) {
return true;
} else {
return false;
}
};

const ld = () => {
if (seconds == 0) {
return false;
} else {
return true;
}
};

return (
<div className="App">
<MainClick
number={number}
handleClick={() => {
setState(prevState => ({
...prevState,
number: prevState.number + x,
HP: prevState.HP - x,
maxnumber: prevState.maxnumber + x
}));
}}
/>
<CountStatistic number={number} HP={HP} bossStats={bossStats} shieldBosses={shieldBosses} defense={defense} />
<Upgrades upgrades={upgrades} handleClick={handleClick} handleClicke={handleClicke} nn={nn} ld={ld} clonNum={clonNum} setSpellState={setSpellState} spellState={spellState} />
</div>
);
}

export default App;
