import "./style.css";
import scooter from "./img/samokat.png";
import React, { useState, useEffect } from 'react';

function App() {
  const [number, setNumber] = useState(0);
  const [num, setNum] = useState(1);
  const [x, setX] = useState(0);
  const [n, setN] = useState(20);
  const [k, setK] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setNumber(prevNumber => prevNumber + x);
    }, 1000);

    return () => clearInterval(timer);
  }, [x]);

  const handleClick = () => {
    setNumber(prevNumber => prevNumber + num);
  };

  const handleClick1 = () => {
    let newNum;

    switch (true) {
      case k > 10:
        newNum = num + 2;
        break;
      case k > 30:
        newNum = num + 4;
        break;
      case k > 20:
        newNum = num + 3;
        break;
      case k > 10:
        newNum = num + 2;
        break;
      default:
        newNum = num + 1;
    }
    setNum(newNum);
    setNumber(prevNumber => prevNumber - n);
    setK(prevK => prevK + 1);
    setN(prevN => prevN * 2);
  };

  const handleClick2 = () => {
    setX(prevX => prevX + 1);
  };

  const handleClick3 = () => {
    setX(prevX => prevX + 2);
  };

  const isButtonDisabled = number < n;

  return (
    <div>
      <div className="mains">
        <div className="container">
          <div className="main_inner">
            <div className="main_click">
              <img src={scooter} alt="" className="main" width="500px" onClick={handleClick} />
              <span className="main_count" id="count">{number}</span>
              <span className="main_count" id="count">За 1 клик: {num}</span>
            </div>
          </div>
        </div>
        <div className="main_up">
          <button className={`btn cl ${isButtonDisabled ? 'noActive' : ''}`} disabled={isButtonDisabled} onClick={handleClick1}>Турбо самокат. Стоимость: {n} Число покупок: {k}</button>
          <button className="btn2 cl" onClick={handleClick2}>Click to buy an upgrade for 200</button>
          <button className="btn3 cl" onClick={handleClick3}>Click to buy an upgrade for 400</button>
        </div>
      </div>
    </div>
  );
}

export default App;
