import "./style.css";
import React, { useState, useEffect } from 'react';
import Button from "./components/button";
import MainClick from "./components/MainScooter";
import CountStatistic from "./components/CountStatistic";

function App() {
  const [bossCount, setBossCount] = useState(1);
  const [HPCount, setHPCount] = useState(700);
  const [maxNumberReached, setMaxNumberReached] = useState(0);
  
  const [state, setState] = useState({
    number: 0,
    num: 1,
    HP: 300,
    x: 0,
    n1: 0,
    n2: 150,
    n3: 300,
    n4: 500,
    threshold1: -1,
    threshold2: 120,
    threshold3: 250,
    threshold4: 400,
    k1: 0,
    k2: 0,
    k3: 0,
    k4: 0
  });

  const { number, num, x, n1, n2, n3, n4, k1, k2, k3, k4, HP, threshold1, threshold2, threshold3, threshold4 } = state;
  const [defense, setDefense] = useState(df())

  if (HP <= 0) {
    setState(prevState => ({ ...prevState, HP: prevState.HP = HPCount }));
    setHPCount(prevCount => prevCount * 4)
    setBossCount(prevCount => prevCount + 1);
    setState(prevState => ({ ...prevState, number: prevState.number + bossCount*500}))
  }

  const df = (c) => {
    if (c == 1){
      return 300
    }
  }

  useEffect(() => {
    const timer = setInterval(() => {
      setState(prevState => ({ ...prevState, number: prevState.number + x, HP: prevState.HP - x, maxnumber: prevState.maxnumber + x }));
    }, 1000);
  
    return () => clearInterval(timer);
  }, [x]);

  useEffect(() =>{
    if (number > maxNumberReached) {
      setMaxNumberReached(number);
    }
  })

  const handleClicke = (index) => {
    const handleClickss = [
      () => {
        
        if ( defense <= 0){
          setState(prevState => ({ ...prevState, number: prevState.number + num, HP: prevState.HP - num}))
        }
      },
      
      () => {
        const purchases = 10;
        const n = k1 > purchases ? 2 : 1;
        handleClicks(0, n1, "n1", "k1");
        setState(prevState => ({ ...prevState, num: num + n }));
      },
      () => handleClicks(1, n2, "n2", "k2"),
      () => handleClicks(2, n3, "n3", "k3"),
      () => handleClicks(3.5, n4, "n4", "k4")
    ];

    const handleClicks = (n, z, cost, k) => {
      setState(prevState => ({
        ...prevState,
        x: prevState.x + n,
        [k]: prevState[k] + 1,
        number: prevState.number - z,
        [cost]: prevState[cost] * 1.1
      }));
    };

    handleClickss[index]();
  };

  return (
    <div>
      <div className="mains">
        <div className="container">
        <CountStatistic x = {x} num = {num} number = {number} />
        <span className="main_count" id="count">Максимальное: {maxNumberReached.toFixed(0)}$</span>
          <div className="main_inner">
            <div className="main_click">
              <span className="main_count" id="count">За 1 клик: {num.toFixed(0)}$</span>
              <span className="main_count" id="count">За 1 секунду: {x.toFixed(0)}$</span>
              <MainClick handleClick = {() => handleClicke(0)} number = {number} bossCount = {bossCount} />
              <span className="main_count red" id="count">HP: {HP.toFixed(0)} Защита: {defense.toFixed(0)}</span>
              <span className="main_count red" id="count">Количество убитых боссов: {bossCount - 1}</span>
            </div>
          </div>
        </div>
        <div className="main_up">
          <Button title="Деревянный самокат" number={number} n={n1} handleClick={() => handleClicke(1)} k={k1} threshold={threshold1} maxnumber={maxNumberReached} />
          <Button title="Медный самокат" number={number} n={n2} handleClick={() => handleClicke(2)} k={k2} threshold={threshold2} maxnumber={maxNumberReached} />
          <Button title="Железный самокат" number={number} n={n3} handleClick={() => handleClicke(3)} k={k3} threshold={threshold3} maxnumber={maxNumberReached} />
          <Button title="Стальной самокат" number={number} n={n4} handleClick={() => handleClicke(4)} k={k4} threshold={threshold4} maxnumber={maxNumberReached} />
        </div>
      </div>
    </div>
  );
}

export default App;
