import React, { useState } from 'react';
import Button from './button';

const Upgrades = ({ number, upgradesData, maxNumberReached, handleClicke, spellDuration }) => {
  const [state, setState] = useState({ upgradesData });

  const handleClicks = (index, n, nKey, kKey, thresholdKey) => {
    const num = number;
    if (num >= upgradesData[thresholdKey]) {
      handleClicke(index);
      const upgrades = { ...state.upgradesData };
      upgrades[nKey] += n;
      upgrades[kKey] += 1;
      setState(prevState => ({
        ...prevState,
        upgradesData: upgrades
      }));
    }
  };

  const { n1, n2, n3, n4, k1, k2, k3, k4, threshold1, threshold2, threshold3, threshold4 } = state.upgradesData;

  return (
    <div className="main_up">
      <div className='main_ups'>
        <Button title="+0.1 урона за клик" number={number} n={n1} handleClick={() => handleClicks(1, 0.1, "n1", "k1", "threshold1")} k={k1} threshold={threshold1} maxnumber={maxNumberReached} spellDuration={spellDuration} />
        <Button title="+0.5$ в секунду" number={number} n={n2} handleClick={() => handleClicks(2, 0.5, "n2", "k2", "threshold2")} k={k2} threshold={threshold2} maxnumber={maxNumberReached} spellDuration={spellDuration} />
        <Button title="+1 DPS" number={number} n={n3} handleClick={() => handleClicks(3, 1, "n3", "k3", "threshold3")} k={k3} threshold={threshold3} maxnumber={maxNumberReached} spellDuration={spellDuration} />
        <Button title="+2.5 DPS" number={number} n={n4} handleClick={() => handleClicks(4, 2.5, "n4", "k4", "threshold4")} k={k4} threshold={threshold4} maxnumber={maxNumberReached} spellDuration={spellDuration} />
      </div>
    </div>
  );
};

export default Upgrades;
